import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getItemById, updateItem } from '../services/itemService'; // Assuming you have these services
import ButtonAppBar from '../components/navbar';

const UpdateItem = () => {
    const { id } = useParams(); // Get the item id from the URL
    const [item, setItem] = useState(null);
    const [updatedItem, setUpdatedItem] = useState({ name: '', price: '' });
    const navigate = useNavigate();

    // Fetch the item details when the component mounts
    useEffect(() => {
        const fetchItem = async () => {
            try {
                const response = await getItemById(id); // Fetch item by id
                setItem(response);
                setUpdatedItem({ name: response.name, price: response.price });
            } catch (error) {
                console.error('Error fetching item:', error);
            }
        };
        fetchItem();
    }, [id]);

    // Handle input change for updating item
    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateItem(id, updatedItem); // Call updateItem service to update the item
            navigate('/items'); // Redirect back to the item list
        } catch (error) {
            console.error('Error updating item:', error);
        }
    };

    if (!item) return <div>Loading...</div>;

    return (
        <div>
            <ButtonAppBar />
            <h1 style={{ textAlign: 'center' }}>Update Item</h1>

            <form onSubmit={handleSubmit} style={{ textAlign: 'center' }}>
                <div>
                    <label>
                        Item Name:
                        <input
                            type="text"
                            name="name"
                            value={updatedItem.name}
                            onChange={handleChange}
                            required
                            style={{ padding: '8px', marginBottom: '10px', width: '200px' }}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Price (PKR):
                        <input
                            type="number"
                            name="price"
                            value={updatedItem.price}
                            onChange={handleChange}
                            required
                            style={{ padding: '8px', marginBottom: '10px', width: '200px' }}
                        />
                    </label>
                </div>
                <div>
                    <button
                        type="submit"
                        style={{
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            padding: '8px 30px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                        }}
                    >
                        Update Item
                    </button>
                </div>
            </form>
        </div>
    );
};

export default UpdateItem;
