import { useNavigate, useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { deleteItem } from '../services/itemService'; // Ensure this function exists
import ButtonAppBar from '../components/navbar';

const DeleteItem = () => {
    const [item, setItem] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchItem = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/items/${id}`);
                if (!response.ok) {
                    throw new Error("Item not found");
                }
                const data = await response.json();
                console.log("Fetched item data:", data); // Add log for debugging
                setItem(data);
                setUpdatedItem({
                    name: data.name,
                    price: data.price,
                    quantity: data.quantity,
                    description: data.description,
                });
            } catch (error) {
                console.error("Error fetching item:", error);
                setItem(null);
            } finally {
                setLoading(false);
            }
        };
        fetchItem();
    }, [id]);


    const handleDelete = async () => {
        try {
            await deleteItem(id);
            navigate('/');
        } catch (error) {
            console.error("Error deleting item:", error);
        }
    };

    if (loading) return <div>Loading...</div>;
    if (!item) return <div>Error: Item not found.</div>;

    return (
        <div style={{ width: '100%' }}>
            <ButtonAppBar />
            <h1>Delete Item</h1>
            <div style={{ padding: '20px', textAlign: 'center' }}>
                <h2>Are you sure you want to delete this item?</h2>
                <p><strong>Item Name:</strong> {item.name}</p>
                <p><strong>Price:</strong> {item.price} PKR</p>
                <p><strong>Quantity:</strong> {item.quantity}</p>
                <div>
                    <button
                        onClick={handleDelete}
                        style={{
                            backgroundColor: '#dc3545',
                            color: 'white',
                            border: 'none',
                            padding: '8px 16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            marginRight: '10px',
                        }}
                    >
                        Delete
                    </button>
                    <button
                        onClick={() => navigate('/')}
                        style={{
                            backgroundColor: '#6c757d',
                            color: 'white',
                            border: 'none',
                            padding: '8px 16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                        }}
                    >
                        Cancel
                    </button>
                </div>
            </div>
        </div>
    );
};

export default DeleteItem;
