import { useState, useEffect } from 'react';
import { getItems } from '../services/itemService';
import ButtonAppBar from '../components/navbar';
import { useNavigate } from 'react-router-dom';

const ItemList = () => {
    const [items, setItems] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        getItems().then(setItems);
    }, []);

    const handleEdit = (id) => {
        navigate(`/update/${id}`);
    };

    const AddItem = () => {
        navigate('/add');
    };

    const DeleteItem = (id) => {
        navigate(`/delete/${id}`);
    };

    return (
        <div width="100%">
            <ButtonAppBar />

            <h1 style={{ textAlign: 'center' }}>Item List</h1>

            <div style={{ display: 'flex', justifyContent: 'center', marginLeft: '40px', marginRight: '40px' }}>
                <table style={{ width: '100%', marginTop: '20px', borderCollapse: 'collapse' }}>
                    <thead>
                        <tr>
                            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Item Name</th>
                            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Price (PKR)</th>
                            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map(item => (
                            <tr key={item._id}>
                                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.name}</td>
                                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{item.price} PKR</td>
                                <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'center' }}>
                                    <button
                                        onClick={() => handleEdit(item._id)}
                                        id={`edit-${item._id}`}
                                        style={{
                                            backgroundColor: '#007bff', color: 'white', border: 'none',
                                            padding: '6px 15px', borderRadius: '4px', cursor: 'pointer', marginRight: '10px'
                                        }}
                                    >
                                        Edit Item
                                    </button>
                                    <button
                                        onClick={() => DeleteItem(item.id)}
                                        id={`delete-${item.id}`}
                                        style={{
                                            backgroundColor: '#dc3545', color: 'white', border: 'none',
                                            padding: '6px 15px', borderRadius: '4px', cursor: 'pointer'
                                        }}
                                    >
                                        Delete Item
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div style={{ textAlign: 'center', marginTop: '50px' }}>
                <button
                    onClick={AddItem}
                    style={{
                        backgroundColor: '#28a745', color: 'white', border: 'none',
                        padding: '10px 40px', borderRadius: '4px', cursor: 'pointer'
                    }}
                >
                    Add Item
                </button>
            </div>
        </div>
    );
};

export default ItemList;
