import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ButtonAppBar from '../components/navbar';
import { updateItem } from '../services/itemService';

const UpdateItem = () => {
    const { id } = useParams(); // Get the item id from the URL
    const [item, setItem] = useState(null);
    // eslint-disable-next-line
    const [loading, setLoading] = useState(true);
    const [updatedItem, setUpdatedItem] = useState({ name: '', price: '', quantity: '', description: '' });
    const navigate = useNavigate();

    // // Fetch the item details when the component mounts
    // useEffect(() => {
    //     const fetchItem = async () => {
    //         try {
    //             const response = await fetch(`http://localhost:5001/api/items/${id}`);
    //             if (!response.ok) {
    //                 throw new Error("Item not found");
    //             }
    //             const data = await response.json();
    //             console.log("Fetched item data:", data); // Add log for debugging
    //             setItem(data);
    //             setUpdatedItem({
    //                 name: data.name,
    //                 price: data.price,
    //                 quantity: data.quantity,
    //                 description: data.description,
    //             });
    //         } catch (error) {
    //             console.error("Error fetching item:", error);
    //             setItem(null);
    //         } finally {
    //             setLoading(false);
    //         }
    //     };
    //     fetchItem();
    // }, [id]);


    // Handle input change for updating item
    const handleChange = (e) => {
        const { name, value } = e.target;
        setUpdatedItem((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Pass both the item id and the updated item data
            await updateItem(id, updatedItem);
            navigate('/'); // Redirect back to the item list
        } catch (error) {
            console.error('Error updating item:', error);
        }
    };


    if (!item) return <div>Loading...</div>;

    return (
        <div>
            <ButtonAppBar />
            <h1 style={{ textAlign: 'center' }}>Update Item</h1>

            <form onSubmit={handleSubmit} style={{ textAlign: 'center' }}>
                <div>
                    <label>
                        Item Name:
                        <input
                            type="text"
                            name="name"
                            value={updatedItem.name}
                            onChange={handleChange}
                            required
                            style={{ padding: '8px', marginBottom: '10px', width: '200px' }}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Price (PKR):
                        <input
                            type="number"
                            name="price"
                            value={updatedItem.price}
                            onChange={handleChange}
                            required
                            style={{ padding: '8px', marginBottom: '10px', width: '200px' }}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Quantity:
                        <input
                            type="number"
                            name="quantity"
                            value={updatedItem.quantity}
                            onChange={handleChange}
                            required
                            style={{ padding: '8px', marginBottom: '10px', width: '200px' }}
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Description:
                        <textarea
                            name="description"
                            value={updatedItem.description}
                            onChange={handleChange}
                            required
                            style={{ padding: '8px', marginBottom: '10px', width: '200px', height: '100px' }}
                        />
                    </label>
                </div>
                <div>
                    <button
                        type="submit"
                        style={{
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            padding: '8px 30px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                        }}
                    >
                        Update Item
                    </button>
                </div>
            </form>
        </div>
    );
};

export default UpdateItem;
